import { useContext, useEffect, useState } from "react"
import { GroupContext } from "../../../Context/GroupContext"
import { Loading } from "../../Loading/Loading";
import axios from 'axios';
import "../Teacher.scss";

export const Homeworks = () => {
    const { group } = useContext(GroupContext);
    const [ selectedGroup, setSelectedGroup ] = useState(null);
    const [ homeworkData, setHomeworkData ] = useState([]);

    const handleGetGroupMember = async (group_id) => {
        try {
            const response = await axios.get(`http://localhost:8080/api/teacher/getHomeworkByGroup/${group_id}`);
            const hmBack = response.data
            const sortedData = hmBack.sort((a, b) => a.lesson_number - b.lesson_number);
            setHomeworkData(sortedData)
        } catch (error) {
            console.error("Error fetching group members:", error);
        }
    }


    const handleGroupChange = (event) => {
        const selectedGroupId = event.target.value;
        setSelectedGroup(selectedGroupId);
        handleGetGroupMember(selectedGroupId);
    };


    return(
        <div>
            <h1 className="teacher__title">Домашнє завдання</h1>
            {group ? (
                <select onChange={handleGroupChange} value={selectedGroup || ""} className="group__select select">
                    <option value={null} className="select__option">Обрати групу</option>
                    {group.map((groupItem) => (
                    <option key={groupItem.group_id} value={groupItem.group_id} className="select__option">
                        {groupItem.name_group} {groupItem.available_lessons}
                    </option>
                    ))}
                </select>
                ) : (
                <Loading></Loading>
            )}

            <table className="homework__table">
                <thead className="homework__thead">
                    <tr className="homework__title homework__tr">
                        <th className="homework__title__item">Номер уроку</th>
                        <th className="homework__title__item">Ім'я та Прізвище</th>
                        <th className="homework__title__item">Статус</th>
                    </tr>
                </thead>
                <tbody className="homework__body">
                    {homeworkData.map((member, index) => (
                    <tr key={index}>
                        <td>{member.lesson_number}</td>
                        <td>{`${member.name} ${member.surname}`}</td>
                        <td>{member.status}</td>
                    </tr>
                    ))}
                </tbody>
            </table>
        </div>    
    )
}
