import { useContext, useEffect, useState } from "react"
import { GroupContext } from "../../../Context/GroupContext"
import { Loading } from "../../Loading/Loading";
import axios from 'axios';
import "./Group.scss";

export const Group = () => {
    const { group } = useContext(GroupContext);
    const [ groupMembers, setGroupMembers ] = useState(null);

    const handleGetGroupMember = async (group_id) => {
        try {
            const response = await axios.get(`http://localhost:8080/api/teacher/getGroupMembers/${group_id}`);
            setGroupMembers(response.data);
        } catch (error) {
            console.error("Error fetching group members:", error);
        }
    }


    return (
        <div>
            {group ? (
                group.map((groupItem, index) => (
                    <p key={index} onClick={() => handleGetGroupMember(groupItem.group_id)} className="group__item">
                        {groupItem.name_group} {groupItem.available_lessons}
                    </p>
                ))
            ) : (
                <Loading></Loading>
            )}

                    
            {groupMembers && (
                <div>
                <p>Учасники групи:</p>
                <ul>
                    {groupMembers.map((member, index) => (
                    <li key={index}>{member.name} {member.surname}</li>
                    ))}
                </ul>
                </div>
            )}

        </div>
    );
};
