import { Header } from "../Header/Header"
import { UserContext } from "../../Context/UserContext"
import { useContext, useState, useEffect } from "react"
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import { Formik, Form, Field, ErrorMessage } from "formik";      
import * as Yup from 'yup';


const validationSchema = Yup.object().shape({
    name: Yup.string()
        .min(2, 'Імя повинно містити принаймі 2 символи')
        .max(15, 'Імя повиино бути не більше 15 букв'),
    surname: Yup.string()
        .min(2, 'Імя повинно містити принаймі 2 символи')
        .max(15, 'Прізвище повиино бути не більше 15 букв'),
    phone: Yup.string()
        .matches(/^\d{10}$/, 'Телефон повинен містити рівно 10 цифр')
})


export const UserPage = () => {

    return(
        <div>
            <Header></Header>
            <div className="user-container">
                <div className="user">
                    <Formik
                            initialValues={{
                                name: '',
                            }}
                            validationSchema={validationSchema}
                            onSubmit={values =>{
                                console.log(values)
                            }}
                            className="user__formik"
                        >
                            <Form className="user__form">
                                <div className="user__input-block">
                                    <label htmlFor="name" className="user__label">Змінити імя:</label>
                                    <Field type="text" id="name" name="name" className="user__input"/>
                                    <ErrorMessage name="name" component="div" className="user__error"/>
                                </div>
                                <button type="submit" className="form__btn">Змінити</button>
                            </Form>
                    </Formik>

                    <Formik
                            initialValues={{
                                surname: '',
                            }}
                            validationSchema={validationSchema}
                            onSubmit={values =>{
                                console.log(values)
                            }}
                            className="user__formik"
                        >
                            <Form className="user__form">
                                <div className="input-block">
                                    <label htmlFor="surname" className="user__label">Змінити прізвище:</label>
                                    <Field type="text" id="surname" name="user__surname" className="user__input"/>
                                    <ErrorMessage name="surname" component="div" className="error"/>
                                </div>
                                <button type="submit" className="form__btn">Змінити</button>
                            </Form>
                    </Formik>
                    
                    <Formik
                            initialValues={{
                                phone: '',
                            }}
                            validationSchema={validationSchema}
                            onSubmit={values =>{
                                console.log(values)
                            }}
                            className="formik"
                        >
                            <Form className="form">
                                <div className="input-block">
                                    <label htmlFor="phone" className="label">Змінити телефон:</label>
                                    <Field type="text" id="phone" name="phone" className="input"/>
                                    <ErrorMessage name="phone" component="div" className="error"/>
                                </div>
                                <button type="submit" className="form__btn">Змінити</button>
                            </Form>
                    </Formik>

                </div>
            </div>
        </div>    
    )
}
